Sage.Entity.Interfaces.IComplaint Complaint =this.BindingSource.Current as Sage.Entity.Interfaces.IComplaint;
Sage.Platform.WebPortal.EntityPage page=(Sage.Platform.WebPortal.EntityPage)Page;

if(page.Request.QueryString["type"] != null)
{
	string lc_EntityType = Convert.ToString(Request.QueryString["type"]);
	Complaint.EntityType = lc_EntityType;
	if(lc_EntityType=="Appeal")
	{
		dtpComplaintDate_lbl.Text="Appeal Date";
		page.TitleBar.Text = "Insert Appeal";		


	}
	else
	{
		dtpComplaintDate_lbl.Text="Complaint Date";
		page.TitleBar.Text = "Insert Complaint";		

	}
}
else
{
	Complaint.EntityType = "Complaint";
}

Sage.Entity.Interfaces.IComplaint complaint = this.BindingSource.Current as Sage.Entity.Interfaces.IComplaint;

complaint.Account = complaint.Contract.Account;

if (complaint.Contract.Contact != null)
{
    complaint.Contact = complaint.Contract.Contact;
}
else
{
    foreach (Sage.Entity.Interfaces.IContact contact in complaint.Contract.Account.Contacts)
    {
        if (contact.IsPrimary == true)
        {
            complaint.Contact = contact;
        }
    }
}    
Sage.Entity.Interfaces.ISalesOrder objsales = Sage.Platform.EntityFactory.GetById<Sage.Entity.Interfaces.ISalesOrder>(complaint.Contract.SalesOrderId);
if (objsales != null)
{
    complaint.SalesOrder = objsales;
 if(complaint.Contract.BranchId != null)
 {
  Sage.Entity.Interfaces.IBranch objbranch = Sage.Platform.EntityFactory.GetById<Sage.Entity.Interfaces.IBranch>(Convert.ToString(complaint.Contract.BranchId)); 
  if (objbranch != null)
   complaint.OriginatingBranch = objbranch.Id.ToString(); 
 }
 if(complaint.Contract.ExecutingBranchId!=null)
 {
  Sage.Entity.Interfaces.IBranch objbranch1 = Sage.Platform.EntityFactory.GetById<Sage.Entity.Interfaces.IBranch>(Convert.ToString(complaint.Contract.ExecutingBranchId)); 
  if (objbranch1 != null)
   complaint.ExecutingBranch = objbranch1.Id.ToString();
 }



}

Sage.Entity.Interfaces.IComplaint complaint = BindingSource.Current as Sage.Entity.Interfaces.IComplaint;
Sage.Platform.Data.IDataService service = 
Sage.Platform.Application.ApplicationContext.Current.Services.Get<Sage.Platform.Data.IDataService>();
System.Data.OleDb.OleDbConnection conObj1 = new System.Data.OleDb.OleDbConnection(service.GetConnectionString());
conObj1.Open();
string query1 = "Select Isnull(MAx(substring(ComplaintNumber,CHARINDEX('/',ComplaintNumber,16)+1,len(ComplaintNumber))),0) +1 cmp from complaint";
System.Data.OleDb.OleDbCommand cmd = new System.Data.OleDb.OleDbCommand(query1, conObj1);
System.Data.DataSet ds = new System.Data.DataSet();
System.Data.OleDb.OleDbDataAdapter da = new System.Data.OleDb.OleDbDataAdapter(query1,conObj1);
da.Fill(ds);

Sage.Entity.Interfaces.IBranch originatorBranch = Sage.Platform.EntityFactory.GetById<Sage.Entity.Interfaces.IBranch>((object)complaint.OriginatingBranch);
Sage.Entity.Interfaces.IBranch executingBranch = Sage.Platform.EntityFactory.GetById<Sage.Entity.Interfaces.IBranch>((object)complaint.ExecutingBranch);
complaint.ComplaintNumber = originatorBranch.BranchName.Substring(0,3) + "/" + complaint.Account.AccountName.Substring(0,3) + "/" + executingBranch.BranchName.Substring(0,3) + "/" + DateTime.Now.Year.ToString() +"/" + ds.Tables[0].Rows[0][0].ToString();
complaint.Save();
System.Web.HttpContext.Current.Response.Redirect(string.Format("~/{0}.aspx?entityId={1}", "Complaint", complaint.Id.ToString()));
