Sage.Entity.Interfaces.ITicket tkt = this.BindingSource.Current as Sage.Entity.Interfaces.ITicket;
string qry = "select CP.contractproductitemID,MAX(isnull(CP.Quantity,0))-isnull(sum(isnull(TP.Quantity,0)),0) Qty From ContractproductItem CP "+
	"Left Join ticketProduct TP ON CP.contractproductitemID = TP.contractproductitemID "+
	"where CP.contractID = '"+ tkt.Contract.Id +"'"+
	"Group By CP.contractproductitemID";
//filter Pincode Lookup 
string sId1 = "'";
Sage.Platform.Data.IDataService service1 = Sage.Platform.Application.ApplicationContext.Current.Services.Get<Sage.Platform.Data.IDataService>();
System.Data.OleDb.OleDbConnection conObj = new System.Data.OleDb.OleDbConnection(service1.GetConnectionString());
System.Data.OleDb.OleDbDataAdapter dataAdapterObj = new System.Data.OleDb.OleDbDataAdapter(qry, conObj);
System.Data.DataTable dt = new System.Data.DataTable();
dataAdapterObj.Fill(dt);
bool flag = false;
foreach (System.Data.DataRow dr in dt.Rows)
{
	if(Convert.ToInt32(dr["Qty"]) != 0)
	{
		flag = false;
		break;
	}
	else
	{
		flag = true;
	}
}
if(flag == true)
{
	throw new Sage.Platform.Application.ValidationException("can not create call, Calls completed for the subjob...");
}
else
{
	//tkt.EndCustomer = Convert.ToString(lueEndCustomer.LookupResultValue);
	tkt.Save();
	System.Web.HttpContext.Current.Response.Redirect(string.Format("~/{0}.aspx?entityId={1}", "Ticket", tkt.Id.ToString()));
